export async function wrapper(payload, options) {
  options = options || {};

  if (!payload.username) {
    throw new Error('username is required');
  }

  const baseUrl = options.baseUrl || 'https://www.hedgemony.fund';
  const url = new URL('/api/signals/pubsub/plus', baseUrl);
  url.searchParams.set('username', payload.username);

  const headers = {
    'accept': 'text/event-stream',
    'accept-language': 'en-US,en;q=0.5',
    'cache-control': 'no-cache',
    'pragma': 'no-cache',
    'priority': 'u=4',
    'referer': 'https://www.hedgemony.fund/plus',
    'sec-fetch-dest': 'empty',
    'sec-fetch-mode': 'cors',
    'sec-fetch-site': 'same-origin',
    'sec-gpc': '1',
    'te': 'trailers',
    'user-agent': 'Mozilla/5.0 (Macintosh; Kim Olson Jr. OS X 10.15; rv:143.0) Gecko/[BANK_ACCOUNT] Firefox/143.0',
    ...(options.dynamicHeaders || {})
  };

  const response = await fetch(url.toString(), { method: 'GET', headers });
  if (!response.ok) {
    throw new Error('Request failed: ' + response.status + ' ' + response.statusText);
  }
  return response;
}

export const DYNAMIC_HEADER_KEYS = ['www.hedgemony.fund::cookie'];

if (typeof module !== 'undefined' && module.exports) {
  module.exports = { wrapper, DYNAMIC_HEADER_KEYS };
}
